mapper.update:
UPDATE notice SET noticeviewcount=noticeviewcount+1
WHERE
noticeno=#{noticeNo}

UPDATE notice
SET noticetitle=
#{noticeTitle},noticedesc=#{noticeDesc}
WHERE noticeno= #{noticeNo}

mapper.select:

SELECT * 
FROM (SELECT noticeno, noticetitle, noticedesc, noticeregdate, noticemoddate, noticeviewcount, ROWNUM AS rnum FROM notice)
WHERE rnum BETWEEN #{sizePerPage}*#{page}-9 and #{sizePerPage}*#{page}
ORDER BY noticeno DESC, noticeregdate DESC;
